import axios from 'axios'
import jwtDecode from 'jwt-decode'
import { cookies, interceptor } from '../app/functions'
import { JWTDecode, Req } from '../interface/Interfaces'


export default class Api {

    private req: Req
    private api = axios.create({
        baseURL: process.env.REACT_APP_BASE_URL,
        withCredentials: true,
        headers: {
            "Content-Type": "application/json",
            "Authorizatio": interceptor()    
        }
    })

    constructor(req: Req | null){ 
        this.req = req!
    }

    get = async (path: string) => {
        try {

            const { id } = this.req

            switch (path) {
                case '/home':
                    const user = await this.api.get(path)
                    return user.data

                case '/login':
                    const response = await this.api.get(path, { params: { id } })
                    return response.data

                default:
                    break
            }

        } catch (error) {
            return error.response.data
        }
    }

    post = async (path: string) => {
        try {
            const response = await this.api.post(path, this.req)
            return response.data
        } catch (error) {
            return error.response.data
        }
    }
}
