import { Props, State, JWTDecode } from '../interface/Interfaces'
import { connect } from 'react-redux'
import { Component } from 'react'
import { withRouter } from 'react-router'
import { mapStateToProps, mapDispatchToProps, cookies } from '../app/functions'
import { Button, Card, Container } from 'react-bootstrap'
import Navigation from '../components/header/Navigation'
import Api from '../api/Api'
import jwtDecode from 'jwt-decode'
import '../assets/css/home.css'



class Home extends Component<Props, State> {

    constructor(props: Props){
        super(props)
        this.state = {

        }
    }

    static getDerivedStateFromProps = async (props: Props, state: State) => {
        const refreshToken = cookies.get('refreshToken')
        const token = cookies.get('token')
        try {
            const currentTime = new Date()
            const decodeToken: JWTDecode = jwtDecode(token)
            if((decodeToken.exp * 1000) < currentTime.getTime()){
                const refresh = await new Api({ refreshToken }).post('/token')
                cookies.set('token', refresh.token)
                const data = await new Api({ token: refresh.token }).get('/home')
                console.log(data)
            }
        } catch (error) {
            console.log(`${error}`)
        }
    }

    componentDidMount = async () => {
        const { isLogin } = this.props.persist
        if(isLogin === false) {
            this.props.history.push('/')
        }
    }

    action = () => {
        console.log(100)
    }

    render = (): JSX.Element => {

        return (
            <>
                <Navigation />
                <Container className="index">
                    <Card>
                        <Card.Body>
                            <Card.Title>Home</Card.Title>
                            <hr/>
                            This is home...
                            <br /><br />
                            <Button onClick={ this.action } variant="dark">Click me!</Button>
                        </Card.Body>
                    </Card>
                </Container>
            </>
        )
    }
}


export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Home))
