import { createSlice } from '@reduxjs/toolkit'


export const hook = createSlice({
    name: 'domasApp',
    initialState: {
        user: '',
        isLogin: false
    },

    reducers: {
        setUser: (state, action) => {
            state.user = action.payload
        },
        setIsLogin: (state, action) => {
            state.user = action.payload
        }
    }
})

export const { 
    setUser
 } = hook.actions

 export default hook.reducer
