import { axiosInstance } from '../app/functions'
import jwtDecode from 'jwt-decode'
import { cookies, interceptor } from '../app/functions'
import { JWTDecode, Req } from '../interface/Interfaces'
import { AxiosInstance, AxiosResponse } from 'axios'


export default class Api {

    private req: Req
    private api: AxiosInstance = axiosInstance

    constructor(req: Req | null){ 
        this.req = req!
    }

    get = async (path: string) => {
        try {

            const { id } = this.req

            switch (path) {
                case '/home':
                    const user: AxiosResponse = await this.api.get(path)
                    return user.data

                case '/login':
                    const response: AxiosResponse = await this.api.get(path, { params: { id } })
                    return response.data

                default:
                    break
            }

        } catch (error) {
            return error.response.data
        }
    }

    post = async (path: string) => {
        try {
            const { data }: AxiosResponse = await this.api.post(path, this.req)
            return data
        } catch (error) {
            return error.response.data
        }
    }
}
