import { Component } from 'react'
import { withRouter } from 'react-router-dom'
import { Props, State } from '../interface/Interfaces'
import { Card, Form, Button } from 'react-bootstrap'
import Navigation from '../components/Navigation'


class Login extends Component<Props, State> {

    constructor(readonly props: Props){
        super(props)
        this.state = {
            email: '',
            password: ''
        }
    }

    formChange = (e: any) => {
        const { name, value } = e.target
        this.setState({ ...this.state, [name]: value })
        console.log(this.state)
    }

    render = (): JSX.Element => {
        return (
            <>
                <Navigation />
                <Card className="login">
                    <h3>Login</h3>
                    <br />
                    <Form.Group>
                        <Form.Control onChange={ this.formChange } type="email" name="email" placeholder="Email" />
                        <br />
                        <Form.Control onChange={ this.formChange } type="password" name="password" placeholder="Password" />
                        <br />
                        <Button variant="dark">Login</Button>
                    </Form.Group>
                </Card>
            </>
        )
    }

}

export default withRouter(Login)
