import { createSlice } from '@reduxjs/toolkit'


export const hook = createSlice({
    name: 'domasApp',
    initialState: {
        user: ''
    },

    reducers: {
        setUser: (state, action) => {
            const { payload } = action
            state.user = payload
        }
    }
})

export const { 
    setUser
 } = hook.actions

 export default hook.reducer
